mapper.update:
update edu_course_note SET
	COURSE_ID = #{courseId},
    KPOINT_ID = #{kpointId},
    USER_ID = #{userId},
    CONTENT = #{content},
    UPDATE_TIME = #{updateTime}
where
    KPOINT_ID = #{kpointId}
    and USER_ID = #{userId}

update edu_course_note 
set edu_course_note.STATUS=#{status}
where edu_course_note.id=#{id}
